import React, { useState } from 'react';
import { Redirect, useHistory } from 'react-router-dom';
import './Login.css';
import PropTypes from 'prop-types';

function handleLogin(username, password, setResponse, e) {
  e.preventDefault();
  const url = 'https://172.22.30.14/user/login';

  return fetch(url, {
    method: 'POST',
    headers: { accept: 'application/json', 'Content-Type': 'application/json' },
    body: JSON.stringify({ username: username, password: password }),
  })
    .then((res) => {
      res.json();
    })
    .then((response) => {
      localStorage.setItem('token', response.token);
      setResponse(response);
    })
    .catch((error) => {
      console.log(error);
      setResponse(error);
    });
}

function LoginAction(props) {
  if (props.response.error) {
    return <p>{props.response.message}</p>;
  } else if (props.response.token) {
    window.location.reload(true);
    return (
      <Redirect
        to={{
          pathname: '/',
        }}
      ></Redirect>
    );
  }
  return <div></div>;
}

export default function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [response, setResponse] = useState('');

  return (
    <div className="wrapper">
      <div className="login-outer-container">
        <h1 className="logo">supplyr</h1>
        <div className="login-container">
          <div className="login-inner-container">
            <h2>User Login</h2>
            <form className="form">
              <input
                placeholder="username..."
                onChange={(e) => setUsername(e.target.value)}
              ></input>
              <input
                type="password"
                placeholder="password..."
                onChange={(e) => setPassword(e.target.value)}
              ></input>
              <button
                type="submit"
                onClick={(e) =>
                  handleLogin(username, password, setResponse(), e)
                }
              >
                Login
              </button>
            </form>
          </div>
        </div>
        <div className="error">
          <LoginAction response={response} />
        </div>
      </div>
    </div>
  );
}
