import React, { useState } from 'react';
import {
  Tab,
  Container,
  Nav,
  Row,
  Col,
  Form,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import './Admin.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const createUser = async (organisationalUnit, username, password) => {
  const url = 'http://localhost:8080/api/v1/users/';

  return fetch(url + organisationalUnit, {
    method: 'POST',
    headers: {
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username: username, password: password }),
  })
    .then((res) => res.json())
    .then((data) => {
      setResponse(data);
    })
    .catch((error) => {
      console.log('errir');
      setResponse(error);
    });
};

export const User = () => {
  const [role, setRole] = useState('ROLE');
  const [organisationalUnit, setOrganisaationalUnit] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  return (
    <Container>
      <div className="admin-card">
        <h2 className="admin-card-title">Create User</h2>
        <Form className="admin-form">
          <p className="admin-input-title">User Details</p>
          <input className="admin-input" placeholder="Username..."></input>
          <input
            className="admin-input"
            type="password"
            placeholder="Password..."
            onChange={(e) => setPassword(e.target.value)}
          ></input>
          {role === 'USER' ? (
            <input
              className="admin-input"
              placeholder="Unit..."
              onChange={(e) => setOrganisaationalUnit(e.target.value)}
            ></input>
          ) : null}
        </Form>
        <DropdownButton title={role} className="user-dropdown">
          <Dropdown.Item onClick={() => setRole('USER')}>USER</Dropdown.Item>
          <Dropdown.Item onClick={() => setRole('ADMIN')}>ADMIN</Dropdown.Item>
        </DropdownButton>
        <Button
          className="user-button "
          onClick={() => createUser(organisationalUnit, username, password)}
        >
          Submit
        </Button>
      </div>
    </Container>
  );
};
