import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import './Login.css';

function LoginAction(props) {
  if (props.response.error) {
    return <p>{props.response.message}</p>;
  } else if (props.response.token) {
    return (
      <Redirect
        to={{
          pathname: '/',
        }}
      ></Redirect>
    );
  }
  console.log('GOOG');
  return <div></div>;
}

export default function Login() {
  const [username, setUsername] = useState();
  const [password, setPassword] = useState();
  const [response, setResponse] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    const url = 'https://172.22.30.14/user/login';

    await fetch(url, {
      method: 'POST',
      headers: {
        accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username: username, password: password }),
    })
      .then((res) => {
        res.json();
      })
      .then((response) => {
        localStorage.setItem('token', response.token);
        console.log(response);
        setResponse(response);
      })
      .catch((error) => {
        console.log(error);
        setResponse(error);
      });
  };

  return (
    <div className="wrapper">
      <div className="login-outer-container">
        <h1 className="logo">supplyr</h1>
        <div className="login-container">
          <div className="login-inner-container">
            <h2>User Login</h2>
            <form className="form">
              <input
                placeholder="username..."
                onChange={(e) => setUsername(e.target.value)}
              ></input>
              <input
                type="password"
                placeholder="password..."
                onChange={(e) => setPassword(e.target.value)}
              ></input>
              <button
                type="submit"
                onClick={(e) =>
                  handleLogin(username, password, setResponse(), e)
                }
              >
                Login
              </button>
            </form>
          </div>
        </div>
        <div className="error">
          <LoginAction response={response} />
        </div>
      </div>
    </div>
  );
}
