import React, { useEffect } from 'react';
import '../Overview/Overview.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookie from 'js-cookie';

import {
  Container,
  Row,
  Col,
  Table,
  Spinner,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';

const fetchAssetData = async () => {
  let token = Cookie.get('token');
  const assetUrl = `http://localhost:8080/api/v1/assets`;
  return fetch(assetUrl, {
    method: 'GET',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
  })
    .then((res) => res.json())
    .then((data) => {
      return data;
    })
    .catch((error) => {
      console.log(error.message);
    });
};

export const AssetDropdown = (props) => {
  const [assetData, setAssetData] = useEffect(null);
  const [loading, setLoading] = useEffect(true);

  useEffect(() => {
    fetchAssetData().then((data) => {
      console.log(data);
      setAssetData(data);
      setLoading(false);
    });
  }, []);

  return (
    <DropdownButton
      variant="secondary"
      className="dropdown"
      id="dropdown-basic-button"
      title={props.selectedAsset}
    >
      {assetData.map((asset, index) => {
        return (
          <Dropdown.Item key={index} onClick={() => props.setAsset(asset.name)}>
            {asset.name}
          </Dropdown.Item>
        );
      })}
    </DropdownButton>
  );
};
