import React, { useState } from 'react';
import {
  Tab,
  Container,
  Nav,
  Row,
  Col,
  Form,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import './Admin.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import styles from '../BootstrapStyles/styles';

const createUser = async (
  organisationalUnit,
  username,
  password,
  role,
  setResponse
) => {
  const url = 'http://localhost:8080/api/v1/users/';
  const token = Cookies.get('token');
  organisationalUnit = role === 'ADMIN' ? 'admin' : organisationalUnit;

  return fetch(url + organisationalUnit, {
    method: 'POST',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username: username, password: password }),
  })
    .then((res) => res.json())
    .then((data) => {
      console.log(data.status);
      setResponse(data);
    })
    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

export const User = () => {
  const [role, setRole] = useState('ROLE');
  const [organisationalUnit, setOrganisaationalUnit] = useState(null);
  const [username, setUsername] = useState(null);
  const [password, setPassword] = useState(null);
  const [response, setResponse] = useState(null);

  return (
    <Container>
      <div className="admin-card">
        <h2 className="admin-card-title">Create User</h2>
        <Form className="admin-form">
          <p className="admin-input-title">User Details</p>
          <input
            className="admin-input"
            placeholder="Username..."
            onChange={(e) => setUsername(e.target.value)}
          ></input>
          <input
            className="admin-input"
            type="password"
            placeholder="Password..."
            onChange={(e) => setPassword(e.target.value)}
          ></input>
          {role === 'USER' ? (
            <input
              className="admin-input"
              placeholder="Unit..."
              onChange={(e) => setOrganisaationalUnit(e.target.value)}
            ></input>
          ) : null}
        </Form>
        {response ? (
          response.status === 'NOT_FOUND' ? (
            <p className="response-status-error">{response.message}</p>
          ) : (
            <p className="response-status-success">User succesfully created!</p>
          )
        ) : null}

        <DropdownButton
          title={role}
          className="user-dropdown"
          variant="secondary"
        >
          <Dropdown.Item
            style={styles.customButton}
            onClick={() => setRole('USER')}
          >
            USER
          </Dropdown.Item>
          <Dropdown.Item onClick={() => setRole('ADMIN')}>ADMIN</Dropdown.Item>
        </DropdownButton>
        {username && password && role != 'ROLE' ? (
          <Button
            style={styles.customButton}
            className="user-button "
            onClick={() => {
              createUser(
                organisationalUnit,
                username,
                password,
                role,
                setResponse
              );
            }}
          >
            Submit
          </Button>
        ) : (
          <Button disabled className="user-button ">
            Submit
          </Button>
        )}
      </div>
    </Container>
  );
};
