import React, { useState } from 'react';
import {
  Tab,
  Container,
  Nav,
  Row,
  Col,
  Form,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import './Admin.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import styles from '../BootstrapStyles/styles';

const createAsset = async (assetName, setResponse) => {
  const url = 'http://localhost:8080/api/v1/assets';
  const token = Cookies.get('token');

  return fetch(url, {
    method: 'POST',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ name: assetName }),
  })
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setResponse(data);
    })
    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

export const Asset = () => {
  const [assetQuantity, setAssetQuantity] = useState(null);
  const [response, setResponse] = useState(null);

  return (
    <Container>
      <div className="admin-card">
        <h2 className="admin-card-title">Create Asset Type</h2>
        <Form className="admin-form">
          <p className="admin-input-title">Asset details</p>

          <input
            className="admin-input"
            placeholder="Asset name..."
            onChange={(e) => setAssetName(e.target.value)}
          ></input>
        </Form>
        {response ? (
          response.status === 'CONFLICT' ? (
            <p className="response-status-error">{response.message}</p>
          ) : (
            <p className="response-status-success">
              Asset succesfully created!
            </p>
          )
        ) : null}

        {assetName ? (
          <Button
            variant="secondary"
            className="user-button "
            onClick={() => {
              createAsset(assetName, setResponse);
            }}
          >
            Submit
          </Button>
        ) : (
          <Button variant="secondary" disabled className="user-button ">
            Submit
          </Button>
        )}
      </div>
      <div className="admin-card">
        <h2 className="admin-card-title">Distribute Assets</h2>
        <Form className="admin-form">
          <p className="admin-input-title">Placement details</p>
          <DropdownButton
            title="{role}"
            className="user-dropdown"
            variant="secondary"
          >
            <Dropdown.Item onClick={() => null}>USER</Dropdown.Item>
            <Dropdown.Item onClick={() => null}>ADMIN</Dropdown.Item>
          </DropdownButton>
          <DropdownButton
            title="{role}"
            className="user-dropdown"
            variant="secondary"
          >
            <Dropdown.Item onClick={() => null}>USER</Dropdown.Item>
            <Dropdown.Item onClick={() => null}>ADMIN</Dropdown.Item>
          </DropdownButton>
        </Form>
        <input
          className="admin-input"
          placeholder="Asset quantity..."
          onChange={(e) => setAssetQuantity(e.target.value)}
        ></input>
        {response ? (
          response.status === 'CONFLICT' ? (
            <p className="response-status-error">{response.message}</p>
          ) : (
            <p className="response-status-success">
              Asset succesfully created!
            </p>
          )
        ) : null}

        {assetName ? (
          <Button
            variant="secondary"
            className="user-button "
            onClick={() => {
              createAsset(assetName, setResponse);
            }}
          >
            Submit
          </Button>
        ) : (
          <Button variant="secondary" disabled className="user-button ">
            Submit
          </Button>
        )}
      </div>
    </Container>
  );
};
