import React, { useEffect, useContext, useState, Component } from 'react';
import { UserContext } from '../../context/AppContext';
import './Overview.css';
import Cookie from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Container,
  Row,
  Col,
  Table,
  Spinner,
  Dropdown,
  DropdownButton,
  ButtonGroup,
} from 'react-bootstrap';
import jwt_decode from 'jwt-decode';
import DropdownItem from 'react-bootstrap/esm/DropdownItem';

const countOrders = (orderList, type) => {
  let numOrders = 0;

  orderList.forEach((order) => {
    if (order.type === type) {
      numOrders++;
    }
  });
  return numOrders;
};

const renderAsset = (asset, index) => {
  return (
    <tr key={index}>
      <td className="table-body">{asset.asset.name}</td>
      <td className="table-body">{asset.quantity}</td>
      <td className="table-body">3</td>
    </tr>
  );
};

const RenderDropdown = (props) => {
  const [activeAsset, setActiveAset] = useState('Select Asset');

  return (
    <DropdownButton
      className="dropdown"
      id="dropdown-basic-button"
      title={activeAsset}
    >
      {props.assetList.map((asset, index) => {
        return (
          <Dropdown.Item onClick={() => setActiveAset(asset.name)}>
            {asset.name}
          </Dropdown.Item>
        );
      })}
    </DropdownButton>
  );
};

const renderBlank = () => {
  return <p>no assets</p>;
};

class Overview extends Component {
  static contextType = UserContext;

  constructor() {
    super();
    this.state = {
      loading: true,
      user: null,
      assets: null,
    };
  }

  async componentDidMount() {
    let token = Cookie.get('token');
    let decoded = jwt_decode(token);
    let username = decoded.sub;

    const UserUrl = `http://localhost:8080/api/v1/users/`;
    const url2 = `http://localhost:8080/api/v1/assets`;
    const userResponse = await fetch(UserUrl + username);
    const userData = await userResponse.json();

    const assetResponse = await fetch(url2);
    const assetData = await assetResponse.json();

    const asset = userData.organisationalUnit.organisationalUnitAssets
      ? userData.organisationalUnit.organisationalUnitAssets
      : null;

    this.setState({
      loading: false,
      user: userData,
      assets: asset,
      assetList: assetData,
      selectedAsset: 'Select asset',
    });
  }

  onCliked(newSelected) {
    this.setState({ selectedAsset: newSelected });
  }

  render() {
    if (this.state.loading || !this.state.user) {
      return (
        <div className="loading-container">
          <Spinner animation="border" variant="secondary" />
        </div>
      );
    } else {
      return (
        <Container className="overview-container">
          <Row className="card-row">
            <Col>
              <div className="card">
                <i className="fas fa-users card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Organisational Unit</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.name}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              <div className="card">
                <i className="fas fa-coins card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Credits</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.credits}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              <div className="card">
                <i className="fas fa-clipboard-list card-icon green"></i>
                <div className="card-inner">
                  <h2 className="card-title">BUY Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'BUY'
                    )}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card">
                <i className="fas fa-clipboard-list card-icon red"></i>
                <div className="card-inner">
                  <h2 className="card-title">SELL Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'SELL'
                    )}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              {' '}
              <div className="card-tall-table">
                <i class="fas fa-boxes card-icon"></i>
                <h3 className="card-title ">Assets</h3>
                <Table striped hover>
                  <thead>
                    <tr className="table-row table-header">
                      <th className="table-header">Name</th>
                      <th className="table-header">Quantity</th>
                      <th className="table-header">Lowest Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.assets
                      ? this.state.assets.map(renderAsset)
                      : renderBlank}
                  </tbody>
                </Table>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card-tall-table">
                <i class="fas fa-store card-icon"></i>
                <h3 className="card-title ">Trade</h3>
                {this.state.assets ? (
                  <RenderDropdown assetList={this.state.assetList} />
                ) : null}
                <input
                  className="quantity-input"
                  placeholder="Quantity"
                ></input>
                <input className="quantity-input" placeholder="Price"></input>
                <DropdownButton>
                  <Dropdown.Item>BUY</Dropdown.Item>
                  <Dropdown.Item>SELL</Dropdown.Item>
                </DropdownButton>
              </div>
            </Col>
          </Row>
        </Container>
      );
    }
  }
}

export default Overview;
