import React, { useEffect, useContext, useState, Component } from 'react';
import { UserContext } from '../../context/AppContext';
import './Overview.css';
import Cookie from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Container,
  Row,
  Col,
  Table,
  Spinner,
  Dropdown,
  DropdownButton,
} from 'react-bootstrap';
import jwt_decode from 'jwt-decode';

const countOrders = (orderList, type) => {
  let numOrders = 0;

  orderList.forEach((order) => {
    if (order.type === type) {
      numOrders++;
    }
  });
  return numOrders;
};

const renderAsset = (asset, index) => {
  return (
    <tr key={index}>
      <td className="table-body">{asset.asset.name}</td>
      <td className="table-body">{asset.quantity}</td>
      <td className="table-body">3</td>
    </tr>
  );
};

const renderDropdownItems = (asset, index) => {
  let assetName = asset.asset.name;
  return (
    <Dropdown.Item onClick={setState({ selectedAsset: assetName })}>
      {assetName}
    </Dropdown.Item>
  );
};

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
    &#x25bc;
  </a>
));

const renderBlank = () => {
  return <p>no assets</p>;
};

class Overview extends Component {
  static contextType = UserContext;

  constructor() {
    super();
    this.state = {
      loading: true,
      user: null,
      assets: null,
    };
  }

  async componentDidMount() {
    let token = Cookie.get('token');
    let decoded = jwt_decode(token);
    let username = decoded.sub;

    const url = `http://localhost:8080/api/v1/users/`;
    const response = await fetch(url + username);
    const data = await response.json();
    console.log(data);
    const asset = data.organisationalUnit.organisationalUnitAssets
      ? data.organisationalUnit.organisationalUnitAssets
      : null;
    this.setState({
      loading: false,
      user: data,
      assets: asset,
      selectedAsset: 'Select asset',
    });
  }

  render() {
    if (this.state.loading || !this.state.user) {
      return (
        <div className="loading-container">
          <Spinner animation="border" variant="secondary" />
        </div>
      );
    } else {
      return (
        <Container className="overview-container">
          <Row className="card-row">
            <Col>
              <div className="card">
                <i className="fas fa-users card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Organisational Unit</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.name}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              <div className="card">
                <i className="fas fa-coins card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Credits</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.credits}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              <div className="card">
                <i className="fas fa-clipboard-list card-icon green"></i>
                <div className="card-inner">
                  <h2 className="card-title">BUY Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'BUY'
                    )}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card">
                <i className="fas fa-clipboard-list card-icon red"></i>
                <div className="card-inner">
                  <h2 className="card-title">SELL Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'SELL'
                    )}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              {' '}
              <div className="card-tall-table">
                <i class="fas fa-boxes card-icon"></i>
                <h3 className="card-title ">Assets</h3>
                <Table striped hover>
                  <thead>
                    <tr className="table-row table-header">
                      <th className="table-header">Name</th>
                      <th className="table-header">Quantity</th>
                      <th className="table-header">Lowest Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.assets
                      ? this.state.assets.map(renderAsset)
                      : renderBlank}
                  </tbody>
                </Table>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card-tall-table">
                <i class="fas fa-store card-icon"></i>
                <h3 className="card-title ">Trade</h3>

                <DropdownButton
                  className="dropdown"
                  id="dropdown-basic-button"
                  title={this.state.selectedAsset}
                >
                  <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                  <Dropdown.Item href="#/action-2">
                    Another action
                  </Dropdown.Item>
                  <Dropdown.Item href="#/action-3">
                    Something else
                  </Dropdown.Item>
                </DropdownButton>
              </div>
            </Col>
          </Row>
        </Container>
      );
    }
  }
}

export default Overview;
