import React from 'react';
import './Orders.css';
import { Container, Row, Col, Table, Button } from 'react-bootstrap';

const deleteOffer = async (props) => {
  const url = `http://localhost:8080/api/v1/offers/delete/`;

  try {
    const config = {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
    };
    const response = await fetch(url + props.orderId, config);

    if (response.ok) {
      return response.json();
      //return json
    } else {
      console.log(response.json());
      //
    }
  } catch (error) {
    //
  }
};

export const Orders = (props) => {
  return (
    <Container className="orders-container">
      <Row className="column">
        <Col className="column">
          <i
            className="fas fa-times-circle icon"
            onClick={() => {
              props.updateClickedState(props.type);
            }}
          ></i>
          <h3 className="title">{props.type} Orders</h3>

          <Table striped hover size="sm">
            <thead>
              <tr className="table-row table-header">
                <th className="table-header ">Asset</th>
                <th className="table-header">Quantity</th>
                <th className="table-header">Price</th>
                <th className="table-header">Action</th>
              </tr>
            </thead>
            <tbody>
              {props.orderList.map((order, index) => {
                if (order.type === props.type) {
                  return (
                    <tr key={index}>
                      <td className="table-body">{order.asset.name}</td>
                      <td className="table-body">{order.quantity}</td>
                      <td className="table-body">{order.price}</td>
                      <td className="table-body">
                        <Button>delete</Button>
                      </td>
                    </tr>
                  );
                }
              })}
            </tbody>
          </Table>
        </Col>
      </Row>
    </Container>
  );
};
