import React, { useState } from 'react';
import {
  Tab,
  Container,
  Nav,
  Row,
  Col,
  Form,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import './Account.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';
import styles from '../BootstrapStyles/styles';

const createUser = async (
  organisationalUnit,
  username,
  password,
  role,
  setResponse
) => {
  const url = 'http://localhost:8080/api/v1/users/';
  const token = Cookies.get('token');
  organisationalUnit = role === 'ADMIN' ? 'admin' : organisationalUnit;

  return fetch(url + organisationalUnit, {
    method: 'POST',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username: username, password: password }),
  })
    .then((res) => res.json())
    .then((data) => {
      console.log(data.status);
      setResponse(data);
    })
    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

export const PasswordReset = () => {
  const [password, setPassword] = useState(null);
  const [response, setResponse] = useState(null);

  return (
    <Container>
      <div className="admin-card">
        <h2 className="admin-card-title">Password Reset</h2>
        <Form className="admin-form">
          <p className="admin-input-title">New password</p>
          <input
            type="password"
            className="admin-input"
            placeholder="New password..."
            onChange={(e) => setPassword(e.target.value)}
          ></input>
        </Form>
        {response ? (
          response.status === 'NOT_FOUND' ? (
            <p className="response-status-error">{response.message}</p>
          ) : (
            <p className="response-status-success">User succesfully created!</p>
          )
        ) : null}

        {password ? (
          <Button
            variant="secondary"
            className="user-button "
            onClick={() => {
              createUser(
                organisationalUnit,
                username,
                password,
                role,
                setResponse
              );
            }}
          >
            Submit
          </Button>
        ) : (
          <Button disabled className="user-button " variant="secondary">
            Submit
          </Button>
        )}
      </div>
    </Container>
  );
};
