import React, { useEffect } from 'react';
import '../Overview/Overview.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookie from 'js-cookie';

import {
  Container,
  Row,
  Col,
  Table,
  Spinner,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';

const fetchAssetData = async () => {
  let token = Cookie.get('token');
  const assetUrl = `http://localhost:8080/api/v1/assets`;
  const assetResponse = await fetch(assetUrl, {
    method: 'GET',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
  });
  const assetData = await assetResponse.json();
  return assetData;
};

export const AssetDropdown = (props) => {
  const [assetData, setAssetData] = useEffect(null);

  useEffect(() => {
    fetchAssetData().then((data) => {
      setAssetData(data);
    });
  }, []);

  return (
    <DropdownButton
      variant="secondary"
      className="dropdown"
      id="dropdown-basic-button"
      title={props.selectedAsset}
    >
      {props.assetList.map((asset, index) => {
        return (
          <Dropdown.Item key={index} onClick={() => props.setAsset(asset.name)}>
            {asset.name}
          </Dropdown.Item>
        );
      })}
    </DropdownButton>
  );
};
