import React, { useEffect, useState } from 'react';
import './Orders.css';
import { Container, Row, Col, Spinner } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import Cookies from 'js-cookie';
import jwtDecode from 'jwt-decode';
import { Chart } from './Chart';

const getAssetPriceData = async (asset, setResponse) => {
  const url = 'http://localhost:8080/api/v1/assets/' + asset + '/trades';
  const token = Cookies.get('token');
  const decoded = jwtDecode(token);

  return fetch(url, {
    method: 'GET',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
  })
    .then((res) => res.json())
    .then((data) => {
      setResponse(data);
      return data;
    })
    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

const getLastSale = (assetData) => {
  const length = assetData.length;
  return assetData[length - 1].price;
};

export const AssetInfo = (props) => {
  const [tradeData, setTradeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [response, setResponse] = useState(null);
  const [dataset, setDataset] = useState(null);
  const [lables, setlabels] = useState(null);

  useEffect(() => {
    getAssetPriceData(props.asset, setResponse).then((data) => {
      setTradeData(data);
      setLoading(false);
    });
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <Spinner animation="border" variant="secondary" />
      </div>
    );
  } else {
    return (
      <Container className="orders-container">
        {' '}
        <i
          className="fas fa-times-circle icon"
          onClick={() => {
            props.updateClickedState('ASSET_FALSE');
          }}
        ></i>
        <h3 className="title">Asset Information</h3>
        {tradeData !== null ? (
          <Row className="column">
            <Col className="column">
              <div className="asset-info-div">
                <Chart tradeData={tradeData} asset={props.asset} />
                <Col className="column">
                  <div className="card-narrow">
                    <p className="items-text">
                      Last Sale Price: {tradeData[tradeData.length - 1].price}
                    </p>
                    <p className="items-text">
                      Number of Sales: {tradeData.length / 2}
                    </p>
                  </div>
                </Col>
              </div>
            </Col>
          </Row>
        ) : null}
      </Container>
    );
  }
};
