import React, { Component } from 'react';
import { Orders } from './Orders';
import { UserContext } from '../../context/AppContext';
import './Overview.css';
import Cookie from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Container,
  Row,
  Col,
  Table,
  Spinner,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import jwt_decode from 'jwt-decode';

const countOrders = (orderList, type) => {
  let numOrders = 0;

  orderList.forEach((order) => {
    if (order.type === type) {
      numOrders++;
    }
  });
  return numOrders;
};

const renderAsset = (asset, index) => {
  return (
    <tr key={index}>
      <td className="table-body">{asset.asset.name}</td>
      <td className="table-body">{asset.quantity}</td>
      <td className="table-body">3</td>
    </tr>
  );
};

const RenderOrderStatus = () => {
  return <p className="orderStatus">Success!</p>;
};

const RenderDropdown = (props) => {
  return (
    <DropdownButton
      className="dropdown"
      id="dropdown-basic-button"
      title={props.selectedAsset}
    >
      {props.assetList.map((asset, index) => {
        return (
          <Dropdown.Item key={index} onClick={() => props.setAsset(asset.name)}>
            {asset.name}
          </Dropdown.Item>
        );
      })}
    </DropdownButton>
  );
};

const renderBlank = () => {
  return <p>no assets</p>;
};

const submitOrder = async (unit, asset, quantity, price, type) => {
  const orderUrl = 'http://localhost:8080/api/v1/offers/';

  try {
    const config = {
      method: 'POST',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        organisationalUnit: unit,
        asset: asset,
        quantity: quantity,
        price: price,
      }),
    };
    const response = await fetch(orderUrl + type.toLowerCase(), config);

    if (response.ok) {
      //return json

      return response.json();
    } else {
      //
      console.log(response.json());
    }
  } catch (error) {
    //
  }
};

class Overview extends Component {
  static contextType = UserContext;

  constructor() {
    super();
    this.state = {
      loading: true,
      user: null,
      assets: null,
      assetList: null,
      selectedAsset: 'Select asset',
      orderType: 'BUY',
      orderQuantity: null,
      orderPrice: null,
      orderResponse: null,
      buyOrdersClicked: false,
    };
    this.updateSelectedAsset = this.updateSelectedAsset.bind(this);
    this.updateBuyClickedState = this.updateBuyClickedState.bind(this);
  }

  async componentDidMount() {
    let token = Cookie.get('token');
    let decoded = jwt_decode(token);
    let username = decoded.sub;

    const userUrl = `http://localhost:8080/api/v1/users/`;
    const assetUrl = `http://localhost:8080/api/v1/assets`;
    const userResponse = await fetch(userUrl + username);
    const userData = await userResponse.json();

    const assetResponse = await fetch(assetUrl);
    const assetData = await assetResponse.json();

    const asset = userData.organisationalUnit.organisationalUnitAssets
      ? userData.organisationalUnit.organisationalUnitAssets
      : null;

    this.setState({
      loading: false,
      user: userData,
      assets: asset,
      assetList: assetData,
    });
  }

  updateSelectedAsset(newAsset) {
    this.setState({ selectedAsset: newAsset });
  }

  updateBuyClickedState() {
    this.setState({ buyOrdersClicked: false });
  }

  render() {
    if (this.state.loading || !this.state.user) {
      return (
        <div className="loading-container">
          <Spinner animation="border" variant="secondary" />
        </div>
      );
    } else {
      return (
        <Container className="overview-container">
          {this.state.buyOrdersClicked ? (
            <Orders updateClickedState={this.updateBuyClickedState} />
          ) : null}
          <Row className="card-row">
            <Col>
              <div className="card">
                <i className="fas fa-users card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Organisational Unit</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.name}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              <div className="card">
                <i className="fas fa-coins card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Credits</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.credits}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              <div
                className="card clickable"
                onClick={() => this.setState({ buyOrdersClicked: true })}
              >
                <i className="fas fa-clipboard-list card-icon green"></i>
                <div className="card-inner">
                  <h2 className="card-title">BUY Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'BUY'
                    )}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card clickable ">
                <i className="fas fa-clipboard-list card-icon red"></i>
                <div className="card-inner">
                  <h2 className="card-title">SELL Orders</h2>
                  <p className="card-text">
                    {countOrders(
                      this.state.user.organisationalUnit.offers,
                      'SELL'
                    )}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row className="card-row">
            {' '}
            <Col>
              {' '}
              <div className="card-tall-table">
                <i className="fas fa-boxes card-icon"></i>
                <h3 className="card-title ">Assets</h3>
                <Table striped hover>
                  <thead>
                    <tr className="table-row table-header">
                      <th className="table-header">Name</th>
                      <th className="table-header">Quantity</th>
                      <th className="table-header">Lowest Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {this.state.assets
                      ? this.state.assets.map(renderAsset)
                      : renderBlank}
                  </tbody>
                </Table>
              </div>
            </Col>
            <Col>
              {' '}
              <div className="card-tall-table">
                <i className="fas fa-store card-icon"></i>
                <h3 className="card-title ">Trade</h3>
                {this.state.assets ? (
                  <RenderDropdown
                    assetList={this.state.assetList}
                    selectedAsset={this.state.selectedAsset}
                    setAsset={this.updateSelectedAsset}
                  />
                ) : null}
                <input
                  type="number"
                  className="quantity-input"
                  placeholder="Quantity"
                  onChange={(e) =>
                    this.setState({ orderQuantity: e.target.value })
                  }
                ></input>
                <input
                  type="number"
                  className="quantity-input"
                  placeholder="Price"
                  onChange={(e) =>
                    this.setState({ orderPrice: e.target.value })
                  }
                ></input>

                <DropdownButton title={this.state.orderType}>
                  <Dropdown.Item
                    onClick={() => this.setState({ orderType: 'BUY' })}
                  >
                    BUY
                  </Dropdown.Item>
                  <Dropdown.Item
                    onClick={() => this.setState({ orderType: 'SELL' })}
                  >
                    SELL
                  </Dropdown.Item>
                </DropdownButton>
                {this.state.orderPrice &&
                this.state.orderQuantity &&
                this.state.selectedAsset !== 'Select asset' ? (
                  <Button
                    className="submit-button"
                    onClick={() =>
                      this.setState({
                        orderResponse: submitOrder(
                          this.state.user.organisationalUnit.name,
                          this.state.selectedAsset,
                          this.state.orderQuantity,
                          this.state.orderPrice,
                          this.state.orderType
                        ),
                      })
                    }
                  >
                    Submit
                  </Button>
                ) : (
                  <Button className="submit-button" disabled>
                    Submit
                  </Button>
                )}
                {this.orderResponse ? <RenderOrderStatus /> : null}
              </div>
            </Col>
          </Row>
        </Container>
      );
    }
  }
}

export default Overview;
