import React, { useEffect, useContext, useState, Component } from 'react';
import { UserContext } from '../../context/AppContext';
import './Overview.css';
import Cookie from 'js-cookie';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';

class Overview extends Component {
  static contextType = UserContext;

  constructor() {
    super();
    this.state = {
      loading: true,
      user: null,
    };
  }

  async componentDidMount() {
    const username = this.context;
    const url = `http://localhost:8080/api/v1/users/Keegan`;
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ loading: false, user: data });
    console.log(this.state.user);
  }

  render() {
    if (this.state.loading || !this.state.user) {
      return <div>loading...</div>;
    } else {
      return (
        <Container>
          <Row>
            <Col>
              <div className="card">
                <i class="fas fa-users card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Organisational Unit</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.name}
                  </p>
                </div>
              </div>
            </Col>
            <Col>
              <div className="card">
                <i class="fas fa-coins card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">Credits</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.credits}
                  </p>
                </div>
              </div>
            </Col>
            <div className="card">
              <i class="fas fa-users card-icon"></i>
              <div className="card-inner">
                <h2 className="card-title">Organisational Unit</h2>
                <p className="card-text">
                  {this.state.user.organisationalUnit.name}
                </p>
              </div>
            </div>
            <Col>
              {' '}
              <div className="card">
                <i class="fas fa-users card-icon"></i>
                <div className="card-inner">
                  <h2 className="card-title">TREE</h2>
                  <p className="card-text">
                    {this.state.user.organisationalUnit.name}
                  </p>
                </div>
              </div>
            </Col>
          </Row>
          <Row></Row>
          <Row></Row>
        </Container>
      );
    }
  }
}

export default Overview;
