import React, { useState } from 'react';
import {
  Tab,
  Container,
  Nav,
  Row,
  Col,
  Form,
  Dropdown,
  DropdownButton,
  Button,
} from 'react-bootstrap';
import './Admin.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'js-cookie';

const createUnit = async (unitName, unitCredits, setResponse) => {
  const url = 'http://localhost:8080/api/v1/organisational-unit';
  const token = Cookies.get('token');

  return fetch(url, {
    method: 'POST',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ name: unitName, credits: unitCredits }),
  })
    .then((res) => {
      if (res.ok) {
        setResponse(res.json());
      } else {
        setResponse(res.json.message);
      }
    })

    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

export const OrganisationalUnit = () => {
  const [unitName, setUnitName] = useState(null);
  const [unitCredits, setUnitCredits] = useState(null);
  const [response, setResponse] = useState(null);

  return (
    <Container>
      <div className="admin-card">
        <h2 className="admin-card-title">Create Organisational Unit</h2>
        <Form className="admin-form">
          <p className="admin-input-title">Unit Details</p>
          <input
            className="admin-input"
            placeholder="Unit name..."
            onChange={(e) => setUnitName(e.target.value)}
          ></input>
          <input
            className="admin-input"
            type="password"
            placeholder="Password..."
            onChange={(e) => setUnitCredits(e.target.value)}
          ></input>
        </Form>
        {response ? (
          response.status === 'NOT_FOUND' ? (
            <p className="response-status-error">{response.message}</p>
          ) : (
            <p className="response-status-success">Unit succesfully created!</p>
          )
        ) : null}

        {unitName && unitCredits ? (
          <Button
            className="user-button "
            onClick={() => {
              createUnit(unitName, unitCredits, setResponse);
            }}
          >
            Submit
          </Button>
        ) : (
          <Button disabled className="user-button ">
            Submit
          </Button>
        )}
      </div>
    </Container>
  );
};
