import React, { useEffect, useState } from 'react';
import './Orders.css';
import { Container, Row, Col } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';

const getAssetPriceData = async (asset) => {
  const url = 'http://localhost:8080/api/v1/assets/' + asset + '/trades';
  const token = Cookies.get('token');
  const decoded = jwtDecode(token);

  return fetch(url + decoded.sub, {
    method: 'GET',
    headers: {
      Authorization: 'Bearer ' + token,
      accept: 'application/json',
      'Content-Type': 'application/json',
    },
  })
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      setResponse(data);
    })
    .catch((error) => {
      console.log(error.message);
      setResponse(error);
    });
};

export const AssetInfo = (props) => {
  const [tradeData, setTradeData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getAssetPriceData(props.asset).then((data) => {
      setTradeData(data);
    });
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <Spinner animation="border" variant="secondary" />
      </div>
    );
  }
  return (
    <Container className="orders-container">
      {' '}
      <Row className="column">
        <Col className="column">
          <i
            className="fas fa-times-circle icon"
            onClick={() => {
              props.updateClickedState('ASSET_FALSE');
            }}
          ></i>
          <h3 className="title">Asset Information</h3>
        </Col>
      </Row>
    </Container>
  );
};
